function [fitresult, gof] = createFit_linear(dilution, cov, T)
%CREATEFIT(DILUTION,COV,T)
%  Create a fit.
%
%  Data for 'Linear polynomial fit' fit:
%      X Input : dilution
%      Y Input : cov
%      Z Output: T
%  Output:
%      fitresult : a fit object representing the fit.
%      gof : structure with goodness-of fit info.
%
%  See also FIT, CFIT, SFIT.

%  Auto-generated by MATLAB on 06-Oct-2013 20:03:28


%% Fit: 'Linear polynomial fit'.
[xData, yData, zData] = prepareSurfaceData( dilution, cov, T );

% Set up fittype and options.
ft = fittype( 'poly22' );
opts = fitoptions( ft );
opts.Lower = [-Inf -Inf -Inf -Inf -Inf -Inf];
opts.Upper = [Inf Inf Inf Inf Inf Inf];

% Fit model to data.
[fitresult, gof] = fit( [xData, yData], zData, ft, opts );

% Plot fit with data.
figure( 'Name', 'Linear polynomial fit' );
h = plot( fitresult, [xData, yData], zData );
legend( h, 'Linear polynomial fit', 'T vs. dilution, cov', 'Location', 'NorthEast' );
% Label axes
xlabel( 'dilution' );
ylabel( 'cov' );
zlabel( 'T' );
grid on
view( 26.5, 44 );
saveas(gcf,'surfacefit_linear','fig')
close(gcf)

